import React, { useContext } from "react";
import { Link } from "react-router-dom";
import UserDropDown from "./UserDropDown";
import UserProvider from "../../contexts/UserProvider";
import { data } from "../../data";
import _ from "lodash";
import PassportLogo from "../../res/passport-1.png";
import HomeIcon from "@material-ui/icons/Home";
import AccountCircleIcon from "@material-ui/icons/AccountCircle";
import LogoutIcon from "@material-ui/icons/MeetingRoom";

const MenuBar = () => {
  const userData = useContext(UserProvider.context);
  const loginType = !_.isEmpty(userData)
    ? _.find(data, (d) => d.name === userData.provider)
    : {};

  return (
    <div className="menu-bar">
      {!_.isEmpty(userData) && (
        <Link
          className="btn menu-btn"
          to="/profile"
          title={`${loginType.name} data`}
        >
          <div
            className="app-icon-container"
            style={{ backgroundColor: loginType.color }}
          >
            <img
              className="btn-icon"
              src={loginType.img}
              alt={loginType.alt}
              style={{ position: "absolute", top: 17, paddingLeft: 5 }}
            />
          </div>
        </Link>
      )}

      {_.isEmpty(userData) && (
        <a className="btn menu-btn disabled" href="/">
          <img
            src={PassportLogo}
            alt="passport.js logo"
            style={{ height: 19 }}
          />
        </a>
      )}

      <Link className="btn menu-btn" to="/" title="Home">
        <HomeIcon />
      </Link>

      {!_.isEmpty(userData) && (
        <Link className="btn menu-btn" to="/profile" title="Profile">
          <AccountCircleIcon />
        </Link>
      )}

      <UserDropDown />

      {!_.isEmpty(userData) && (
        <a
          className="btn menu-btn"
          href={"/logout"}
          title="Logout"
          style={{ float: "right" }}
        >
          <LogoutIcon />
        </a>
      )}
    </div>
  );
};

export default MenuBar;
