import React, { Component } from 'react'
import axios from 'axios'
import "./Form.css";

class PostForm extends Component {
	constructor(props) {
		super(props)

		this.state = {
			Amount: '',
			title: '',
			body: ''
		}
	}

	changeHandler = e => {
		this.setState({ [e.target.name]: e.target.value })
	}

	submitHandler = e => {
		e.preventDefault()
		console.log(this.state)
		axios
			.post('https://jsonplaceholder.typicode.com/posts', this.state)
			.then(response => {
				console.log(response)
			})
			.catch(error => {
				console.log(error)
			})
	}

	render() {
		const { Amount, title, body } = this.state
		return (

			/**


      <form onSubmit={handleSubmit} className="form" noValidate>
        <div className="form-inputs">
          <label className="form-label">Amount</label>
          <input
            className="form-input"
            type="amount"
            name="amount"
            placeholder="Enter your amount in rupees"
            value={values.amount}
            onChange={handleChange}
          />
          {errors.amount && <p>{errors.amount}</p>}
        </div>
        <button className="form-input-btn" type="submit" onClick={add()}>
          Add
        </button>
        <button className="form-input-btn" type="submit" onClick={withdraw()}>
          Withdraw
        </button>
        <div style={{ height: 25 }}></div>
      </form>
    </div>
			 */
			<div className="form-content-right">
				      <h1>Get Your account details</h1>
      <button className="form-input-btn-balance" onClick={null}>
        Get Balance
      </button>
				<form onSubmit={this.submitHandler} className="form">
					<div >
					<label className="form-label">Amount</label>
						<input className="form-input"
							type="text"
							name="Amount"
							value={Amount}
							onChange={this.changeHandler}
						/>
					</div>
					<div>
						<input
							type="text"
							name="title"
							value={title}
							onChange={this.changeHandler}
						/>
					</div>
					<div>
						<input
							type="text"
							name="body"
							value={body}
							onChange={this.changeHandler}
						/>
					</div>
					<button type="submit">Submit</button>
				</form>
			</div>
		)
	}
}

export default PostForm
