import react, { Component } from "react";
import axios from "axios";

class Balance extends Component {
  constructor(props) {
    super(props);
    this.state = {
      Balance: [],
    };
  }

  componentDidMount() {
    //   TOCHANGE
    axios
      .get("https://jsonplaceholder.typicode.com/posts/")
      .then((res) => {
        console.log(res);
        this.setState({ Balance: res.data });
        // console.log(Balance.toString);
      })
      .catch();
  }

  render() {
    const { Balance } = this.state;
    return (
      <div>
        List of balance
        {
          // <div>{Balance.forEach(bal=>{<div key={bal.id}>{bal.title}</div>})}</div>

          Balance.length
            ? Balance.map((bal) => {
                <div key={bal.id}>{bal.title}</div>;
              })
            : null
        }
      </div>
    );
  }
}

export default Balance;
