    super(props);

    this.state = {
      totalBalance: null,
    };
  }

  componentDidMount() {
    axios
      // .get("https://api.npms.io/v2/search?q=react")
      .get("https://piggybankbackend.mybluemix.net/customers/2")
      .then((response) =>
        this.setState({ totalBalance: response.data.total })
      );
  }

