import React, { useContext, useState } from "react";
// import logo from ".././logo.svg";
import "../App.css";
// import Logoutcard from "../components/cards/LogoutCard";
// import Footer from "../components/footer/Footer";
import LogoutIcon from "../../src/res/logout.png";
import FacebookIcon from "../res/facebook.png";
import GithubIcon from "../res/github.png";
import GoogleIcon from "../res/google.png";

import LogoutButton from "../components/buttons/IconButton";
import UserProvider from "../contexts/UserProvider";
import _ from "lodash";

import Terminal from "../components/displays/Terminal";
import Col from "../components/wrappers/Col";
import DataTags from "../components/menus/DataTags";


const Profile = () => {

  const LoginMsg =
  "Uh oh, there's nothing to show! " +
  "Login to see how much of your invaluable personal " +
  "data tech companies have at their disposal.";

  // if(){}
  const app = {
    img: LogoutIcon,
    href: "http://localhost:4321/logout",
    alt: "logout-icon",
    colors: "#5BDAFB",
    txt: "Logout",
    name: "Logout",
  };

  const [selected, setSelected] = useState("All");
  const userData = useContext(UserProvider.context);
  const text = _.isEmpty(userData) ? LoginMsg : "Enjoy Piggy bank services...";
  const selectedData = selected === "All" ? userData : userData[selected];
  const jsonCode = JSON.stringify(selectedData, null, 4);

  const options = Object.keys(userData).filter((key) => {
    return userData[key] !== null;
  });

  return (
    <div className="page">
      <p className="page-title" style={{ textAlign: "center" }}>
        {text}
      </p>
      <p className="page-title" style={{ textAlign: "center" }}>
        {jsonCode}
      </p>

      <Col className="col-4" style={{ verticalAlign: "top" }}>
        <DataTags
          options={options}
          onClick={(option) => setSelected(option)}
          selected={selected}
        />
      </Col>

      <Col className="col-8">
        <Terminal userData={userData} selected={selected} />
      </Col>
      <LogoutButton app={app}  onClick={userData=null}/>
      <div style={{ marginBottom: 20 }} />
    </div>
  );
  // return (
  //   <div className="App">
  //     <header className="App-header">
  //       <img src={logo} className="App-logo" alt="logo" />
  //       <h1>Welcome to Piggy bank</h1>
  //       <p className="page-title" style={{ textAlign: "center" }}>
  //         {text}
  //       </p>
  //       <p>Enjoy Piggy Bank Services</p>
  //     </header>
  //     <LogoutButton app={app} />
  //     <div style={{ marginBottom: 70 }} />
  //   </div>
  // );
};
export default Profile;
