import React from "react";
import validate from "./validateInfo";
import useForm from "./useForm";
import axios from "axios";
import "./Form.css";

const FormSignup = ({ submitForm }) => {
  const { handleChange, handleSubmit, values, errors } = useForm(
    submitForm,
    validate
  );

  const handleBalance = (e) => {
    e.preventDefault();
    // console.log(this.state);
    axios
      .post("https://jsonplaceholder.typicode.com/posts/1", this.state)
      .then((response) => {
        console.log(response);
      })
      .catch((error) => {
        console.log(error);
      });
  };

  function withdraw() {
    values.transcationMode = -1;
  }

  function add() {
    values.transcationMode = +1;
  }

  function getBalance() {

    axios
    .get("https://jsonplaceholder.typicode.com/posts")
    .then((response) => {
      console.log(response);
    })
    .catch((error) => {
      console.log(error);
    });


    // axios
    // .get('https://jsonplaceholder.typicode.com/posts')
    // .then(response => {
    //   console.log(response)
    //   this.setState({ posts: response.data })
    // })
    // .catch(error => {
    //   console.log(error)
    //   this.setState({errorMsg: 'Error retrieving data'})
    // })
  }

  return (
    <div className="form-content-right">

      <h1>Your account balance is</h1>
      <button className="form-input-btn-balance"
        type="submit" onClick={getBalance()}>
      Get Balance
        </button>
      {/* <button
        className="form-input-btn-balance"
        type="submit"
        // onSubmit={handleSubmit}

        onClick={
          {
            getBalance()
          }
        }
      >
        Get Balance
      </button> */}
      <form onSubmit={handleSubmit} className="form" noValidate>
        <div className="form-inputs">
          <label className="form-label">Amount</label>
          <input
            className="form-input"
            type="amount"
            name="amount"
            placeholder="Enter your amount in rupees"
            value={values.amount}
            onChange={handleChange}
          />
          {errors.amount && <p>{errors.amount}</p>}
        </div>
        <button className="form-input-btn" type="submit" onClick={add()}>
          Add
        </button>
        <button className="form-input-btn" type="submit" onClick={withdraw()}>
          Withdraw
        </button>
        <div style={{ height: 25 }}></div>
      </form>
    </div>
  );
};

export default FormSignup;
