import React, { Component } from "react";
import axios from "axios";

class GETbalance extends Component {
  constructor(props) {
    super(props);

    this.state = {
      posts: [],
      errorMsg: "",
    };
  }

  componentDidMount() {
    // https://piggybankbackend.mybluemix.net/customers/
    // https://jsonplaceholder.typicode.com/posts
    axios
      .get("https://jsonplaceholder.typicode.com/posts")
      .then((response) => {
        console.log(response.data);
        this.setState({ posts: response.data });
      })
      .catch((error) => {
        console.log(error);
        this.setState({ errorMsg: "Error retrieving data" });
      });
  }

  render() {
    const { posts, errorMsg } = this.state;
    return (
      <div>
        List of posts
        <div>
          {
            console.log(),
          console.log(JSON.parse(JSON.stringify(posts))[0])
          }
        </div>
        {posts.length
          ? <div>{JSON.parse(JSON.stringify(posts))[0]}</div> // posts.map((post) => <div key={post.id}>{post.name}</div>)
          : null}
        {errorMsg ? <div>{errorMsg}</div> : null}
      </div>
    );
  }
}

export default GETbalance;
